<?php
namespace Setka\Editor\Admin\Pages\Settings\SetkaEditor;

use Setka\Editor\Admin\Prototypes\Pages;
use Setka\Editor\Plugin;
use Setka\Editor\Admin\Options;
use Setka\Editor\Admin\Transients;
use Setka\Editor\Service;

class SetkaEditorPage extends Pages\VirtualMenuPage {

	public function __construct() {
		if(Service\SetkaAccount\Account::is_logged_in()) {
			$this->setVirtualPage(new Account\AccountPage());
		} else {
			$this->setVirtualPage(new SignUp\SignUpPage());
		}
		$this->getVirtualPage()->setParentPage($this);

		$this->setPageTitle($this->getVirtualPage()->getPageTitle());
		$this->setMenuTitle(__('Setka Editor', Plugin::NAME));
		$this->setCapability('manage_options');
		$this->setMenuSlug(Plugin::NAME);
		$this->setIcon('data:image/svg+xml;base64,');
		$this->setPosition(81);

		$this->setName(Plugin::NAME);

		add_action('admin_head', array($this, 'fixMenu'));
	}

	public function fixMenu() {
		global $submenu;
		if(isset($submenu[$this->getName()][0][0])) {
			// Update first menu item title
			$submenu[$this->getName()][0][0] = $this->getVirtualPage()->getMenuTitle();
		}
	}

	public function enqueueScriptStyles() {
		wp_enqueue_script(Plugin::NAME . '-wp-admin-setting-pages-initializer');
		wp_enqueue_style(Plugin::NAME . '-wp-admin-main');
	}
}
